-- for PostgreSQL
drop table if exists movie_ts;

CREATE TABLE movie_ts (
    title varchar(100),
    year integer,
    rating real,
    revenue_millions real,
    genre varchar(100),
    description varchar(500),
    director varchar(200),
    actors varchar(200),
    genre_a text[],
    genre_j jsonb,
    actors_a text[],
    actors_j jsonb
);


